const pipe = fns => x => fns.reduce((v, f) => f(v), x);


const mapDOM = scope => ({
  sortLikes: scope.querySelector("#likes"),
  sortComments: scope.querySelector("#comments"),
  sortHeadline: scope.querySelector("#headline"),
  inputHeadline: scope.querySelector("input[type='text']"),
  myTable: scope.querySelector(".mytable")
});

const capitalizeFirstLetter = string =>
  string.charAt(0).toUpperCase() + string.slice(1);

const jsonToTable = (json) => {
  const cols = Object.keys(json[0]);
  let headerRow = "";
  let bodyRows = "";

  cols.map(col =>
    headerRow += "<th>" + 
    capitalizeFirstLetter(col) + "</th>"
  );

  json.map(row => {
    bodyRows += "<tr>";

    cols.map(colName =>
      bodyRows += "<td>" +
      row[colName] + "</td>"
    );
    bodyRows += "</tr>";
  });

  return `<table class="mytable">
  <thead>
  <tr>
    ${headerRow}
  </tr>
  </thead>
  <tbody>${bodyRows}</tbody>
  </table>
  `
};

const html = props => console.log(props) ||
  `
  <div>
  <span>Brought to you by Datastack.de | </span>
  <a href="https://www.datastack.de">Check out all our services</a>
  </div>
  <hr>
  <p>Start searching</p>
  <input type="text" placeholder="Enter Headline">
  <button id="likes">Sort by Likes</button>
  <button id="comments">Sorty by Comments</button>
  <br>
  <br>
  <br>
  ${jsonToTable(props)}

  `;


const css = props => html =>
  `
  ${html}
  <style>
  </style>
  `;

const render = props => pipe([
  html,
  css ()
]) (props);

class Table extends HTMLElement {
  constructor() {
    super();
    this._headline = "";
    this.attachShadow({mode: "open"});
    this.root = this.shadowRoot;
    this.root.innerHTML = render(json);
    this.dom = mapDOM(this.root);

    this.dom.sortLikes.addEventListener("click", e => {
      if (this._headline !== "") {
        const output = json.filter(entry => entry.headline.includes(this._headline)); 
        const sorted = output.sort((a, b) => b.likes - a.likes);
        this.dom.myTable.innerHTML = jsonToTable(sorted);
      } else  {
        const sorted = json.sort((a, b) => b.likes - a.likes);
        this.dom.myTable.innerHTML = jsonToTable(sorted);
      }
    });

    this.dom.sortComments.addEventListener("click", e => {
      if (this._headline !== "") {
        const output = json.filter(entry => entry.headline.includes(this._headline)); 
        const sorted = output.sort((a, b) => b.comments - a.comments);
        this.dom.myTable.innerHTML = jsonToTable(sorted);
      } else  {
        const sorted = json.sort((a, b) => b.comments - a.comments);
        this.dom.myTable.innerHTML = jsonToTable(sorted);
      }
    });

    this.dom.inputHeadline.addEventListener("input", e => {
      this._headline = e.target.value;
      const output = json.filter(entry => entry.headline.includes(this._headline)); 
      output.length > 0
      ? this.dom.myTable.innerHTML = jsonToTable(output) 
      : void 7;
    });
    }
}


!customElements.get("cio-table") 
  ? customElements.define("cio-table", Table)
  : void 7;

export default Table;
